mapper.select:
SELECT F.POST_NO,F.NM,F.TITLE,F.HIT,F.DT,F.CONTENTS,F.IMG_FILE,F.IMG_FILE FROM(SELECT G.POST_NO,M.NM,G.TITLE,G.HIT,G.DT,G.CONTENTS,G.RNUM,G.IMG_FILE FROM (SELECT POST_NO,TITLE,HIT,CONTENTS,MEMBER_NO,B.IMG_FILE, CASE WHEN TO_CHAR(B.REG_DT, 'YY.MM.DD') = TO_CHAR(SYSDATE,'YY.MM.DD') THEN TO_CHAR(B.REG_DT, 'HH24:MI') ELSE TO_CHAR(B.REG_DT,'YY.MM.DD') END AS DT, ROW_NUMBER() OVER(ORDER BY B.POST_NO DESC) AS RNUM FROM BOARD_TYPE T INNER JOIN BULLETIN_BOARD B ON T.BLTNBOARD_NO = B.BLTNBOARD_NO WHERE B.DEL_DT IS NULL AND T.BLTNBOARD_NO =3 )G INNER JOIN MEMBER M ON G.MEMBER_NO = M.MEMBER_NO
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND CONTENTS LIKE '%'|| #{searchTxt}||'%'
AND NM LIKE '%'|| #{searchTxt} ||'%'
)F WHERE F.RNUM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE BULLETIN_BOARD SET HIT = HIT+1
WHERE BLTNBOARD_NO =3
AND POST_NO= #{no}
