mapper.select:
SELECT F.POST_NO,F.BLTNBOARD_NM,F.TITLE,F.REG_DT, F.CONTENTS FROM(SELECT B.POST_NO, T.BLTNBOARD_NM, B.TITLE, B.CONTENTS, CASE WHEN TO_CHAR(B.REG_DT, 'YY.MM.DD') = TO_CHAR(SYSDATE,'YY.MM.DD') THEN TO_CHAR(B.REG_DT, 'HH24:MI') ELSE TO_CHAR(B.REG_DT,'YY.MM.DD') END AS REG_DT, ROW_NUMBER() OVER(ORDER BY B.POST_NO DESC) AS RNUM FROM BULLETIN_BOARD B INNER JOIN BOARD_TYPE T ON B.BLTNBOARD_NO = T.BLTNBOARD_NO WHERE B.DEL_DT IS NULL
AND T.BLTNBOARD_NO = #{cate}
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND CONTENTS LIKE '%'|| #{searchTxt}||'%'
)F WHERE F.RNUM BETWEEN #{start} AND #{end}

SELECT C.POST_NO, C.TITLE, C.CONTENTS, C.REG_DT FROM(SELECT B.POST_NO, B.TITLE, C.CONTENTS, CASE WHEN TO_CHAR(C.REG_DT, 'YY.MM.DD') = TO_CHAR(SYSDATE,'YY.MM.DD') THEN TO_CHAR(C.REG_DT, 'HH24:MI') ELSE TO_CHAR(C.REG_DT,'YY.MM.DD') END AS REG_DT, ROW_NUMBER() OVER(ORDER BY B.POST_NO DESC) AS RNUM FROM BULLETIN_BOARD B INNER JOIN COMMENTS C ON B.POST_NO = C.POST_NO WHERE C.DEL_DT IS NULL
AND B.BLTNBOARD_NO = #{cate}
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND CONTENTS LIKE '%'|| #{searchTxt}||'%'
)C WHERE C.RNUM BETWEEN #{start} AND #{end}
