mapper.select:
SELECT POST_NO,MEMBER_NO,TITLE,ATT_FILE,HIT,MUST_READ_YN,DEL_DT,DT,RNUM,CONTENTS FROM (SELECT POST_NO,MEMBER_NO,TITLE,ATT_FILE,HIT,MUST_READ_YN,DEL_DT,CONTENTS, CASE WHEN TO_CHAR(REG_DT, 'YY.MM.DD') = TO_CHAR(SYSDATE,'YY.MM.DD') THEN TO_CHAR(REG_DT, 'HH24:MI') ELSE TO_CHAR(REG_DT,'YY.MM.DD') END AS DT,ROW_NUMBER() OVER(ORDER BY POST_NO DESC) AS RNUM FROM NOTICE WHERE DEL_DT IS NULL
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND TITLE CONTENTS '%' || #{searchTxt} || '%'
)F WHERE RNUM BETWEEN #{start} AND #{end}
