mapper.select:
SELECT MEMBER_NO, AUTHORITY_NM, NM, EMAIL FROM (SELECT M.MEMBER_NO, A.AUTHORITY_NO, AUTHORITY_NM, NM, EMAIL, ROW_NUMBER()OVER(ORDER BY M.MEMBER_NO ASC) AS RNK FROM MEMBER M INNER JOIN AUTHORITY A ON M.AUTHORITY_NO = A.AUTHORITY_NO WHERE M.DEL_DT IS NULL AND A.AUTHORITY_NO IN (2, 3)
AND A.AUTHORITY_NO = #{cate}
AND MEMBER_NO LIKE '%' || #{searchTxt} || '%'
AND NM LIKE '%' || #{searchTxt} || '%'
AND EMAIL LIKE '%' || #{searchTxt} || '%'
) AM WHERE RNK BETWEEN #{start} AND #{end}

mapper.update:
		UPDATE MEMBER SET AUTHORITY_NO = #{aut}, NM = #{nm}, PWD = #{pwd}, PHONE_NO = #{phn}, 
                          DATE_BIRTH = #{dtBrt}, GENDER = #{gen}, ZIP_CODE = #{zcd}, 
                          ADDRESS = #{adr}, DETAIL_ADDRESS = #{adrDtl}
		WHERE MEMBER_NO = #{updNo}
