mapper.select:
SELECT POST_NO, TITLE, CONTENTS, NM, TO_CHAR(N.REG_DT, 'YYYY-MM-DD') AS REG_DT, ATT_FILE, MUST_READ_YN, RNK FROM(SELECT POST_NO, TITLE, CONTENTS, NM, N.REG_DT, ATT_FILE, MUST_READ_YN, ROW_NUMBER()OVER(ORDER BY N.REG_DT DESC) AS RNK FROM NOTICE N INNER JOIN MEMBER M ON N.MEMBER_NO = M.MEMBER_NO WHERE N.DEL_DT IS NULL AND MUST_READ_YN = 0 ) N WHERE RNK <= 3 UNION ALL SELECT POST_NO, TITLE, CONTENTS, NM, TO_CHAR(N.REG_DT, 'YYYY-MM-DD') AS REG_DT, ATT_FILE, MUST_READ_YN, RNK FROM(SELECT POST_NO, TITLE, CONTENTS, NM, N.REG_DT, ATT_FILE, MUST_READ_YN, ROW_NUMBER()OVER(ORDER BY N.REG_DT DESC) AS RNK FROM NOTICE N INNER JOIN MEMBER M ON N.MEMBER_NO = M.MEMBER_NO WHERE N.DEL_DT IS NULL AND MUST_READ_YN = 1
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND CONTENTS LIKE '%' || #{searchTxt} || '%'
) N WHERE RNK BETWEEN #{start} AND #{end}
