mapper.select:
SELECT A.POST_NO, A.TITLE, A.NM, A.REG_DT,A.RNK FROM( SELECT N.POST_NO, N.TITLE, N.CONTENTS, M.NM, N.REG_DT, ROW_NUMBER()OVER(ORDER BY N.REG_DT DESC) AS RNK FROM NOTICE N INNER JOIN MEMBER M ON N.MEMBER_NO = M.MEMBER_NO WHERE N.DEL_DT IS NULL) A WHERE A.RNK BETWEEN #{start} AND #{end}
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND CONTENTS LIKE '%' || #{searchTxt} || '%'
