mapper.select:
SELECT A.REPORT_NO, A.REPORT_MEMBER_NO, A.TARGET_REPORT_MEMBER_NO, A.PROCESS_NO, A.REPORT_TYPE_NM, A.CATE ,A.REG_DT FROM(SELECT R.REPORT_NO, R.REPORT_MEMBER_NO, R.TARGET_REPORT_MEMBER_NO, R.PROCESS_NO, RT.REPORT_TYPE_NM, CASE WHEN COMMENT_NO IS NOT NULL AND POST_NO IS NOT NULL THEN '댓글' WHEN COMMENT_NO IS NULL AND POST_NO IS NOT NULL THEN '글' ELSE '회원' END AS CATE, R.REG_DT, ROW_NUMBER() OVER(ORDER BY R.REG_DT DESC)AS RNK FROM REPORT R INNER JOIN REPORT_TYPE RT ON R.REPORT_TYPE_NO = RT.REPORT_TYPE_NO LEFT JOIN MEMBER P ON R.TARGET_REPORT_MEMBER_NO = P.MEMBER_NO LEFT JOIN MEMBER C ON R.REPORT_MEMBER_NO = C.MEMBER_NO WHERE R.DEL_DT IS NULL
AND M.MEMBER_NO LIKE '%' || #{searchTxt} || '%'
AND B.POST_NO LIKE '%' || #{searchTxt} || '%'
AND M.NM LIKE '%' || #{searchTxt} || '%'
AND B.TITLE LIKE '%' || #{searchTxt} || '%'
)A WHERE A.RNK BETWEEN #{start} AND #{end}
