mapper.select:
SELECT A.REPORT_NO, A.REPORT_MEMBER_NO,A.ACCUSER, A.TARGET_REPORT_MEMBER_NO, A.ACCUSED, A.PROCESS_NO, A.REPORT_TYPE_NM, A.CATE ,A.REG_DT FROM ( SELECT R.REPORT_NO, R.REPORT_MEMBER_NO, C.NM AS ACCUSER, R.TARGET_REPORT_MEMBER_NO,P.NM AS ACCUSED, R.PROCESS_NO, RT.REPORT_TYPE_NM, CASE WHEN COMMENT_NO IS NOT NULL AND POST_NO IS NULL AND TARGET_REPORT_MEMBER_NO IS NULL THEN '댓글' WHEN COMMENT_NO IS NULL AND POST_NO IS NOT NULL AND TARGET_REPORT_MEMBER_NO IS NULL THEN '글' ELSE '회원' END AS CATE, R.REG_DT, ROW_NUMBER() OVER(ORDER BY R.REG_DT DESC)AS RNK FROM REPORT R INNER JOIN REPORT_TYPE RT ON R.REPORT_TYPE_NO = RT.REPORT_TYPE_NO LEFT JOIN MEMBER P ON R.TARGET_REPORT_MEMBER_NO = P.MEMBER_NO LEFT JOIN MEMBER C ON R.REPORT_MEMBER_NO = C.MEMBER_NO WHERE R.DEL_DT IS NULL
AND CATE LIKE '%' || #{searchTxt} || '%'
AND ACCUSER '%' || #{searchTxt} || '%'
AND ACCUSED LIKE '%' || #{searchTxt} || '%'
AND REPORT_TYPE_NM LIKE '%' || #{searchTxt} || '%'
)A WHERE A.RNK BETWEEN #{start} AND #{end}
