mapper.select:
SELECT F.POST_NO,F.COURSE_NO,F.COURSE_NM,F.BLTNBOARD_NO,F.MEMBER_NO,F.TITLE,F.CONTENTS,F.B_IMG,F.HIT,F.DT,F.RNUM,F.GOOD,F.NM FROM(SELECT BC.POST_NO,BC.COURSE_NO,C.COURSE_NM,BB.BLTNBOARD_NO,M.MEMBER_NO,BB.TITLE,BB.CONTENTS,BB.IMG_FILE AS B_IMG,BB.HIT,M.NM, CASE WHEN TO_CHAR(BB.REG_DT, 'YY.MM.DD') = TO_CHAR(SYSDATE,'YY.MM.DD') THEN TO_CHAR(BB.REG_DT, 'HH24:MI') ELSE TO_CHAR(BB.REG_DT,'YY.MM.DD') END AS DT, ROW_NUMBER() OVER(ORDER BY BB.POST_NO DESC) AS RNUM, NVL(CNT,0)AS GOOD FROM COURSE C INNER JOIN BOARD_COURSE BC ON C.COURSE_NO=BC.COURSE_NO INNER JOIN BULLETIN_BOARD BB ON BC.POST_NO=BB.POST_NO INNER JOIN MEMBER M ON BB.MEMBER_NO=M.MEMBER_NO LEFT OUTER JOIN ( SELECT COUNT(*)AS CNT,POST_NO FROM GOOD GROUP BY POST_NO) G ON BB.POST_NO= G.POST_NO WHERE BLTNBOARD_NO=1
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND CONTENTS LIKE '%'|| #{searchTxt}||'%'
AND NM LIKE '%'|| #{searchTxt} ||'%'
)F WHERE F.RNUM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE BULLETIN_BOARD SET HIT = HIT+1
WHERE BLTNBOARD_NO =2
AND POST_NO= #{no}
