mapper.select:
SELECT MEMBER_NO, EMAIL, PWD, NM, PHONE_NO, TO_CHAR(DATE_BIRTH, 'YYYY-MM-DD') AS DATE_BIRTH, GENDER, ZIP_CODE, ADDRESS, DETAIL_ADDRESS, IMG_FILE
FROM MEMBER
WHERE MEMBER_NO = #{memNo}

SELECT MEMBER_NO, NM
FROM MEMBER
WHERE DEL_DT IS NULL 
AND AUTHORITY_NO = 2
AND PWD = #{pwd}
AND MEMBER_NO = #{memNo}

mapper.update:
UPDATE MEMBER SET NM = #{nm}, PHONE_NO = #{phn}, DATE_BIRTH = #{dtBrt, jdbcType = DATE}, GENDER = #{gen, jdbcType = VARCHAR}, ZIP_CODE = #{zcd, jdbcType = VARCHAR}, 
ADDRESS =  #{adr, jdbcType = VARCHAR}, DETAIL_ADDRESS = #{adrDtl, jdbcType = VARCHAR}, IMG_FILE = #{imgFile, jdbcType = VARCHAR}
WHERE MEMBER_NO = #{memNo}
AND PWD = #{cnfmPwd}

mapper.select:
SELECT EMAIL
FROM MEMBER
WHERE DEL_DT IS NULL
AND NM = #{findNm}
AND PHONE_NO = #{findPhn}

SELECT MEMBER_NO
FROM MEMBER
