mapper.select:
SELECT * FROM (SELECT MEM_NO, MEM_ID, MEM_NM, CASE WHEN TO_CHAR(REG_DT, 'YY.MM.DD') = TO_CHAR(SYSDATE, 'YY.MM.DD') THEN TO_CHAR(REG_DT, 'HH24:MI') ELSE TO_CHAR(REG_DT, 'YY.MM.DD') END AS REG_DT, ROW_NUMBER() OVER(ORDER BY MEM_NO DESC) AS RNK FROM MEM WHERE DEL = 1
AND MEM_ID LIKE '%' || #{searchTxt} || '%'
AND MEM_NM LIKE '%' || #{searchTxt} || '%'
) M WHERE M.RNK BETWEEN #{start} AND #{end}

SELECT MEM_NO, MEM_ID, MEM_NM, TO_CHAR(MEM_BIRTH, 'YYYY-MM-DD') AS MEM_BIRTH, TO_CHAR(REG_DT, 'YYYY-MM-DD') AS REG_DT
FROM MEM
WHERE MEM_NO = #{no}

mapper.update:
UPDATE MEM SET MEM_ID = #{id},

MEM_PW = #{pw},	

 MEM_NM = #{nm}, MEM_BIRTH = #{birth}
WHERE MEM_NO = #{no}
